<template>
<div>
      <section class="app-cinema-details">
            <!-- 因为单线程 刚开始的时候从上往下加载 还没获取到info  所以先让 info为false 先不渲染 等获取到了数据在进行渲染 -->
            <div class="cinema-info" v-if="info">
                  <div class="title line-ellipsis">{{info.cinemaData.nm}}</div>
                  <div class="location line-ellipsis">{{info.cinemaData.addr}}</div>
                  <div class="location-icon" data-bid="dp_wx_cinema_map"><img src="data:image/png;base64,"></div>
            </div>
      </section>
      <app-cinema-details-title></app-cinema-details-title>
</div>
</template>
  
<script>
import AppCinemaDetailsTitle from "@com/common/app-cinema-details/AppCinemaDetailsTitle";
export default {
components: {
     AppCinemaDetailsTitle
    },

    props: ["id"], //获取到ID
    data() {
        return {
            info: null
        };
    },
    async created() {
        let result = await this.$http({
            url: "/my/ajax/cinemaDetail",
            params: { cinemaId: this.id }, // ？ 后面跟的参数
            react: false
        });
        console.log(result);
        this.info = result;

        // this.info.name
    }
};
</script>

